// The Typescript type interface for a resume
interface Resume {
  id: string;
  // Company name, job title and job description are optional values
  companyName?: string;
  jobTitle?: string;
  jobDescription?: string;
  feedback: Feedback | null; // The feedback can be null when a object of type Resume is created before the AI generates a review
}

// The Typescript type interface for the AI Feedback
interface Feedback {
  overallScore: number;
  ATS: {
    score: number;
    tips: {
      type: "good" | "improve";
      tip: string;
    }[];
  };
  toneAndStyle: {
    score: number;
    tips: {
      type: "good" | "improve";
      tip: string;
      explanation: string;
    }[];
  };
  content: {
    score: number;
    tips: {
      type: "good" | "improve";
      tip: string;
      explanation: string;
    }[];
  };
  structure: {
    score: number;
    tips: {
      type: "good" | "improve";
      tip: string;
      explanation: string;
    }[];
  };
  skills: {
    score: number;
    tips: {
      type: "good" | "improve";
      tip: string;
      explanation: string;
    }[];
  };
}
